mapper.update:
UPDATE USER U, MEMBER M
SET U.PWD = #{ pwd }
    , U.EMAIL = #{ email }
    , U.PHONE = #{ phone }
    , M.NICKNAME = #{ nickname }
WHERE U.USER_ID = MEMBER.USER_ID
    AND U.USER_ID = #{ userId }

mapper.delete:
DELETE
FROM BOARD
WHERE BOARD_CODE = #{ boardCode }

DELETE
FROM MEMBER_COMMENT
WHERE COMMENT_CODE = #{ commentCode }

DELETE
FROM REVIEW
WHERE BOOKING_CODE = #{ bookingCode }
