mapper.update:
UPDATE USER U
SET PWD = #{ pwd }
    , EMAIL = #{ email }
    , PHONE = #{ phone }
WHERE U.USER_ID = #{ userId }

UPDATE MEMBER M
SET NICKNAME = #{ nickname }
WHERE M.USER_ID = #{ userId }

mapper.select:
SELECT A.MEMBER_NICKNAME
FROM MEMBER A
WHERE A.MEMBER_NICKNAME = #{ nickname }

SELECT COUNT(*)
FROM BOARD
WHERE USER_ID = #{ sessionId }

SELECT COUNT(*)
FROM MEMBER_COMMENT
WHERE USER_ID = #{ sessionId }

SELECT COUNT(*)
FROM REVIEW
WHERE USER_ID = #{ sessionId }

mapper.delete:
DELETE
FROM BOARD
WHERE BOARD_CODE = #{ boardCode }

DELETE
FROM MEMBER_COMMENT
WHERE COMMENT_CODE = #{ commentCode }

DELETE
FROM REVIEW
WHERE BOOKING_CODE = #{ bookingCode }
