mapper.select:
SELECT COUNT(*)
FROM USER U
WHERE U.USER_ID = #{ userId }

SELECT COUNT(*)
FROM MEMBER M
WHERE M.NICKNAME = #{ nickname }

SELECT USER_ID
        FROM USER
        WHERE USER_ID = #{ userId }

mapper.insert:
INSERT INTO
            USER  (
                      NAME
                    , USER_ID
                    , GENDER
                    , PWD
                    , EMAIL
                    , PHONE
                    , REGIST_DATE
                    , AUTHORITY_CODE
                    )
        VALUES (
                  #{ name }
                , #{ userId }
                , #{ gender }
                , #{ pwd }
                , #{ email }
                , #{ phone }
                , #{ registDate }
                , #{ authorityCode }
                )

mapper.update:
INSERT INTO MEMBER (USER_ID, NICKNAME)
SELECT U.USER_ID, #{ nickname }
FROM USER U
WHERE U.USER_ID = #{ userId };

mapper.insert:
INSERT INTO THERAPIST (
                        USER_ID
                        ,THERAPIST_Q1
                        ,THERAPIST_Q2
                        ,THERAPIST_Q3
                        ,ORGANIZATION
                        ,DEPRESSION_CK
                        ,ANXIETY_CK
                        ,OCD_CK
                        ,BIPOLAR_CK
                        ,RELATION_CK
                        ,SESSION_VIDCALL_CK
                        ,SESSION_CHAT_CK
                        ,SESSION_INPERSON_CK
                        )
VALUES (
        #{ userId }
        , #{ therapistQ1 }
        , #{ therapistQ2 }
        , #{ therapistQ3 }
        , #{ organization }
        , #{ depressionCK }
        , #{ anxietyCK }
        , #{ ocdCK }
        , #{ bipolarCK }
        , #{ relationCK }
        , #{ sessionVidCallCK }
        , #{ sessionChatCK }
        , #{ sessionInPersonCK }
)

mapper.update:
INSERT INTO DATA_FILE (
                        USER_ID
                        , FILE_NUMBER
                        , FILE_PATH
                        , FILE_ORIGIN_NAME
                        , FILE_CHANGED_NAME
                        , FILE_SIZE
) SELECT T.USER_ID
                        , #{fileNumber}
                        , #{filePath}
                        , #{fileOriginName}
                        , #{fileChangedName}
                        , #{fileSize}
FROM THERAPIST T
WHERE T.USER_ID = #{ userId };
